import React, { useState, useEffect } from 'react';
import axios from 'axios';

function DateRangeForm(props) {
  const [billingDate, setBillingDate] = useState(null);
  const [selectedDateRange, setSelectedDateRange] = useState(null);

  useEffect(() => {
    const fetchBillingDate = async () => {
      const response = await axios.post('https://www.zoelindev.com/moneyCatBackend/dateRangeForm.php', { sessionEmail: props.sessionEmail });
      const data = response.data;
      setBillingDate(data.billing);
    };

    fetchBillingDate();
  }, [props.sessionEmail]);

  useEffect(() => {
    if (billingDate) {
      const billStartDate = new Date(billingDate);
      const monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
      const options = [];

      for (let i = 0; i < 12; i++) {
        const monthStartDate = new Date(billStartDate.getFullYear(), billStartDate.getMonth() + i, billStartDate.getDate());
        const monthEndDate = new Date(billStartDate.getFullYear(), billStartDate.getMonth() + i + 1, billStartDate.getDate() - 1);
        const optionLabel = `${monthNames[monthStartDate.getMonth()]} ${billStartDate.getDate()} ~ ${monthNames[monthEndDate.getMonth()]} ${monthEndDate.getDate()}`;

        options.push({
          startDate: monthStartDate.toISOString(),
          endDate: monthEndDate.toISOString(),
          label: optionLabel
        });
      }

      setSelectedDateRange(options[0]);
      setOptions(options);
    }
  }, [billingDate]);

  const [options, setOptions] = useState([{startDate: "", label: "Choose date range"}]);

  const handleDateRangeChange = (event) => {
    const selectedOption = options.find(option => option.startDate === event.target.value);
    setSelectedDateRange(selectedOption);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Do something with selectedDateRange
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Select a date range:
        <select value={selectedDateRange ? selectedDateRange.startDate : ''} onChange={handleDateRangeChange} required>
          {options.map(option => (
            <option key={option.startDate} value={option.startDate}>{option.label}</option>
          ))}
        </select>
      </label>
      <button type="submit">Submit</button>
    </form>
  );
}

export default DateRangeForm;
