import { Link } from "react-router-dom";
import Add from "../components/add";
import React from 'react';
import { useState } from "react";

const spendType = ['food','shop','home','lend','health','travel','learn','fun','others'];

const [style, setStyle] = useState("small");
// const clickStyle ={
//     transform: scale(1.5)
//   };

class InputSpend extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value:''
        };

        this.chooseType = this.chooseType.bind(this);
      }
      chooseType(e) {
        this.setState({value: e.target.id})
        alert(this.state.value);
        setStyle("bigger");
      };

// const InputSpend = () => {

    render() {

    return(
        <div className="inputSpendPage">
            <div>
                <Link to="/input_spend">Spend</Link>
                <Link to="/input_income">Income</Link>
            </div>

                <div className="inputBox_grid">
                {spendType.map(value => 
                <button id={value} onClick={this.chooseType}>
                    <img 
                    id={'img'+ value} 
                    src={'/image/Icons/'+ value +'.svg'}
                    className={style}
                    />
                </button>
                )}     
                </div>
                <Add />
        </div>
        );
    }
}

export default InputSpend;
