import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function List(props) {
    const [isOpen, setIsOpen] = useState(false);
    const [spendings, setSpendings] = useState([]);
    const sessionEmail = localStorage.getItem("email");

    function handleClick() {
      setIsOpen(!isOpen);
    }

    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await axios.post('https://www.zoelindev.com/moneyCatBackend/typelist.php', {
            email: sessionEmail,
            startDate: props.startDate,
            endDate: props.endDate,
            type: props.type
          });
          setSpendings(response.data);
        } catch (error) {
          console.error(error);
        }
      };
      fetchData();
    }, [sessionEmail, props.startDate, props.endDate, props.type]);

    const icon = isOpen ? faCaretUp : faCaretDown;

    return (
      <div className="detail" style={{ display: isOpen ? 'block' : 'none' }}>
        {Array.isArray(spendings) && spendings.map((spending) => (
          <div key={spending.id} className="listItem">
            <div className="listTop">
              <span className="listTitle">{spending.title}</span>
              <span className="listAmount">{spending.amount}</span>
            </div>
            <div className="listBottom">
              <span className="listDate">{spending.date}</span>
            </div>
          </div>
        ))}
      </div>
    );
  }  

export default List;
