import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function List(props) {
  const [spendings, setSpendings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post('https://www.zoelindev.com/moneyCatBackend/spendings.php', {
          email: props.email,
          type: props.type,
          startDate: props.startDate,
          endDate: props.endDate
        });
        setSpendings(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
        setIsError(true);
      }
    };
    fetchData();
  }, [props.email, props.type, props.startDate, props.endDate]);

  const handleDelete = async (spendingId) => {
    try {
      await axios.post('https://www.zoelindev.com/moneyCatBackend/deleteSpending.php', { spendingId: spendingId });
      setSpendings(spendings.filter(s => s.spendingId !== spendingId));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {isLoading && <p>Loading...</p>}
      {isError && <p>Something went wrong!</p>}
      {!isLoading && !isError &&
        <ul>
          {spendings.map((spending) => (
            <li key={spending.spendingId}>
              {spending.date} {spending.description} ${spending.amount}
              <FontAwesomeIcon icon={faTimes} className="deleteIcon" onClick={() => handleDelete(spending.spendingId)} />
            </li>
          ))}
        </ul>
      }
    </div>
  );
}

export default List;
