import { useState } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";


const Start = () => {
    // const [email, setEmail]= useState('');
    // const [password, setPassword]= useState('');
    // const [name, setName] = useState('');

    let history = useNavigate();

    const [newUser, setNewUser] = useState ({
        name:"",
        email:'',
        password:''
    })

    const handleChange = (e) => {
        setNewUser({...newUser, [e.target.name]: e.target.value});
        //console.log(newUser);
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("submit works");

        const sendNewUser = {
            name:newUser.name,
            email:newUser.email,
            password:newUser.password
        }
        console.log(sendNewUser);

        axios.post('https://www.zoelindev.com/moneyCatBackend/insert.php',sendNewUser).then((result) =>{
        if(result.newUser.status === 'Invalid') {
          alert('Invalid User')
        }else {
          history(`./overview`);
        }
      })     

    // fetch('/api/user', {
    //     method: 'POST',
    //     headers: { 'Content-Type': 'application/json' },
    //     body: JSON.stringify(sendNewUser)
    //   })
    //   .then(response => response.json())
    //   .then(data => {
    //     if (data.status === 'success') {
    //       // Redirect to the overview page upon successful insertion of the new user
    //       history(`/overview`);
    //     } else {
    //       alert('Failed to create new user');
    //     }
    //   })
    //   .catch(error => {
    //     console.error('Error inserting new user', error);
    //     alert('Failed to create new user');
    //   });

    }

    return(
        <div className="startPage">

                <div className="round2"></div>
                <img src="/image/lazyCat.png" alt="Lazy Cat" className="lazyCat" width="2000"
            height="2000"/>

        <h1>Get Start</h1>
        <p>Create your account now!</p>

        <form onSubmit={handleSubmit}>
            <input
            type="text"
            id="name"
            className="name"
            name="name"
            aria-label="User" placeholder="Name" 
            required 
            value={newUser.name}
            onChange={handleChange}
            />

            <input 
            type="email" 
            id="email" 
            className="email" 
            name="email"
            aria-label="Email" placeholder="Email" 
            required 
            value={newUser.email}
            onChange={handleChange}
            />

            <input 
            type="text" 
            id="password" 
            className="password" 
            name="password"
            aria-label="Password" placeholder="Password" 
            required 
            value={newUser.password}
            onChange={handleChange}
            />

            <input type="submit" value="Sign up"/>
        </form>


        </div>
    );
}

export default Start;
