import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown, faCaretUp, faCircle } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

function Open(props) {
  const [isOpen, setIsOpen] = useState(false);
  const sessionEmail = localStorage.getItem("email");
  const sessionStartDate = localStorage.getItem("startDate");
  const sessionEndDate = localStorage.getItem("endDate");
  const [cost, setCost] = useState({});

  function handleClick() {
    setIsOpen(!isOpen);
  }

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post('https://www.zoelindev.com/moneyCatBackend/typelist.php', {
          email: sessionEmail,
          startDate: sessionStartDate,
          endDate: sessionEndDate
        });
        setCost(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, [sessionEmail, sessionStartDate, sessionEndDate]);

  useEffect(() => {
    const calculateTotalCost = async () => {
      try {
        const response = await axios.post('https://www.zoelindev.com/moneyCatBackend/typelist.php', {
          email: sessionEmail,
          startDate: sessionStartDate,
          endDate: sessionEndDate
        });
        const totalCost = Object.values(response.data).reduce((acc, curr) => acc + curr, 0);
        props.setCost(totalCost);
      } catch (error) {
        console.error(error);
      }
    };
    calculateTotalCost();
  }, [sessionStartDate, sessionEndDate, props.setCost, sessionEmail]);

  const icon = isOpen ? faCaretUp : faCaretDown;
  const circle = faCircle;
  const circleId = 'circle_'+ props.title;

  return (
    <div>
      <div onClick={handleClick}>
        <FontAwesomeIcon icon={icon} />
        <FontAwesomeIcon icon={circle} id={circleId}/>
        <span className='subtitle'>{props.title} ${props.cost}</span>
      </div>
      {isOpen && props.children}
    </div>
  );
}

export default Open;
