import Header from "../components/Header";
import { ReactSession } from 'react-client-session';
import axios from 'axios';
import { useNavigate } from "react-router-dom";

const Overview = () => {

    let direct = useNavigate();

    const [totalCost, setTotalCost] = useState('');
    const [totalEarn, setTotalEarn] = useState('');
    const [balance, setBalance] = useState('');

    axios.get('https://www.zoelindev.com/moneyCatBackend/print.php').then((response)=>{
        if(response.data === 1) {
            //alert('Please log in to your account.')
            direct(`/login`);
        }else { 
            setTotalCost(totalCost.data);
            setTotalEarn(totalEarn.data);
            setBalance(totalEarn - totalCost);
            console.log("Total cost: "+totalCost);
            console.log("Total earn: "+totalEarn);
            console.log("Total balance: "+balance);

        }
        })

    return(
        <div className="overviewPage">
            <Header/>
            <img src="/image/sleeping.jpg" alt="A gril is sleeping with a fat cat" className="sleeping" width="1920"
            height="2559"/>

            <div className="flex_direction_grid">
                <p>Balance: {balance}</p>
                <div className="flex">
                    <p>Spend: {totalCost}</p>
                    <p>Income: {totalEarn}</p>
                </div>
            </div>
            <footer>
                <img src="/image/icons/tray.svg" alt="Icon for monthly chart" className="tray"/>

                <img src="/image/icons/overCat.svg" alt="Icon for overview page" className="overCat"/>

                <img src="/image/icons/calculater.svg" alt="Icon for bookkeeping calculater" className="calculater"/>
            </footer>

        </div>
    );
}

export default Overview;
