import { useState } from "react";


const Add = (props) => {

    const [note, setNote]= useState('');
    const [date, setDate]= useState('');
    // const [result, setResult]= useState('');
    const [amount, setAmount]= useState('');

    const updateAmount = value =>{
        setAmount(amount+value);
    }

    const createDigits = () => {
        const digits = [];
        for (let i = 1; i < 10; i++) {
            digits.push(
                <button 
                key={i} 
                onClick={()=> updateAmount(i.toString())}>{i}
                </button>
            )       
        }
        return digits;
    }

    const ac = () => {
        if (amount === ""){
            return;
        }
        setAmount('');
    }

    return(
	<div className="addWrap">
        <div className="row1">
            <input 
                type="date" 
                id="date" 
                className="date" 
                name="date"
                aria-label="date" placeholder="Date"
                required 
                value={date}
                onChange={(e)=> setDate(e.target.value)}
                />

            <input 
                type="text" 
                id="note" 
                className="note" 
                name="note"
                aria-label="note" placeholder="Click to write a note" 
                value={note}
                onChange={(e)=> setNote(e.target.value)}
                />
        </div>
        <div className="row2">
            <div className="display">
                <span>$ </span>
                <span id="total" value={amount}>{amount || '0'}</span>


            </div>

            <button onClick={()=> updateAmount('0')}>0</button>

            <button onClick={()=> ac()}>AC</button>
        </div>

        <div className="digits">
            {createDigits()}
        </div>

        <div className="rowRight">
            <button onClick={()=> updateAmount('.')}>.</button>
            <button type="submit" onClick={props.handleSubmit}>ADD</button>
        </div>

	</div>
    );
}

export default Add;
