import { Link } from "react-router-dom";
import Add from "../components/add";
import React from 'react';
import { useState } from "react";

const incomeType = [
"salay",
"bonus",
"sideline",
"inves",
"allowance",
"stock",
"tips",
"othersIn"]

const InputIncome = () => {
    const [type, setType]= useState('');

    const beforeStyle =  {
        width: '20%',

      };

    const selectStyle = {
        width: '20%',
        border: "2px solid rgb(204,123,105)",

    }

    const handleClick = (type) => {
        setType(type);
    }

    const handleSubmit = (note,date,amount) => {console.log("submit",type,note,date,amount)};


    return(
        <div className="inputIncomePage">
            <div>
                <Link to="/input_spend" className="spend">Spend</Link>
                <Link to="/input_income" className="income" >Income</Link>
            </div>
            <div className="inputBox_grid">
                {incomeType.map(value => 
                <button style={type == value ? selectStyle : beforeStyle} id={value} onClick={()=>handleClick(value)}>
                    <img 
                    //id={'img'+ value} 
                    src={'/image/Icons/'+ value +'.svg'}
                    alt={'This is '+ value +' option.'} 
                    />
                </button>
                )}     
            </div>
            <Add handleSubmit={handleSubmit}/>
        </div>
    );
}

export default InputIncome;
