import { Link } from "react-router-dom";
import Add from "../components/add";
import React from 'react';

const spendType = ['food','shop','home','lend','health','travel','learn','fun','others'];



class InputSpend extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            value:'',
            width: 30
        };

        this.clickType = this.clickType.bind(this);
      }
      clickType(e) {
        this.setState({value: e.target.id})
        alert(this.state.value);
        document.getElementById(this.state.value).style.cssText = `
        transform: scale(1.5)
                `;
      };


// const InputSpend = () => {

    render() {
        const style = {
            width: this.state.width
          }
    return(
        <div className="inputSpendPage">
            <div>
                <Link to="/input_spend">Spend</Link>
                <Link to="/input_income">Income</Link>
            </div>

                <div className="inputBox_grid">
                {spendType.map(value => 
                <button style={style} id={value} onClick={this.clickType}>
                    <img 
                    //id={'img'+ value} 
                    src={'/image/Icons/'+ value +'.svg'}
                    alt={'This is '+ value +' option.'} 
                    />
                </button>
                )}     
                </div>
                <Add />
        </div>
        );
    }
}

export default InputSpend;
