import { Link } from "react-router-dom";
import Header from "../components/Header";
import React from 'react';
import Open from '../components/Open';
import DateRangeForm from "../components/DateRangeForm";
import axios from "axios";


const ChartSpend = (props) => {

    const sessionEmail = localStorage.getItem("email");

    const handleRemoveItem = async (itemId) => {
        await axios.post('https://www.zoelindev.com/moneyCatBackend/deleteItem.php', {
            email: sessionEmail, 
            id: itemId
        });
        setSpendDetails(spendDetails.filter(item => item.id !== itemId));
    };

    return (
        <div className="chartWrap">
            <Header/>
            <div className="round3"></div>
            <h2>Monthly</h2>

            <div className="op">
                <Link to="/chart_spend" className="chartSpendBtn">
                Spend / 
                </Link>
                <Link to="/chart_income" className="chartIncomeBtn">
                Income
                </Link>
            </div>

            <div className="top">
                {/* <Doughnut data={data} className="chart"/>; */}
                <DateRangeForm email={sessionEmail} />
            </div>

            <div className="spendDetail">
                <Open title="Food">
                {spendDetails.filter(item => item.category === 'Food').map(item => (
                    <div key={item.id}>
                        <span>{item.date} {item.note} ${item.cost} </span>
                        <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
                    </div>
                ))}
                </Open>
                <Open title="Home">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Fun">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Travel">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Health">
                    <p>Hidden content for Food goes here</p>
                </Open>

                <Open title="Learn">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Shop">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Lend">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Others">
                    <p>Hidden content for Food goes here</p>
                </Open>
            </div>
        </div>
    );
}

export default ChartSpend;
