import { Link } from "react-router-dom";
import Add from "../components/add";
import React from 'react';
import { useState } from "react";
import axios from "axios";


const spendType = ['food','shop','home','lend','health','travel','learn','fun','others'];



const InputSpend = () => {

    const [type, setType]= useState('');

    const beforeStyle =  {
        width: '20%',

      };

    const selectStyle = {
        width: '20%',
        border: "2px solid rgb(204,123,105)",

    }

    const handleClick = (type) => {
        setType(type);
    }
    const sessionEmail = localStorage.getItem("email");
    console.log(sessionEmail);

    const handleSubmit = (note,date,amount) => {
        console.log("spend",type,date,note, '$'+amount);

        axios.post("https://www.zoelindev.com/moneyCatBackend/spend.php" , JSON.stringify({ 
            "email": localStorage.getItem("email"),
            "type": type,
            "date": document.querySelector("#date").value,  
            "note": document.querySelector("#note").value,  
            "amount": document.querySelector("#amount").value,  
        })).then( (response) => { 
            console.log(response); 
        }).catch( error => { 
            console.log(error); 
        })  
    };

    return(
        <div className="inputSpendPage">
            <div>
                <Link to="/input_spend" className="spend">Spend</Link>
                <Link to="/input_income" className="income" >Income</Link>
            </div>

                <div className="inputBox_grid">

                {spendType.map((value,index) => 
                <button style={type === value ? selectStyle : beforeStyle} id={value} onClick={()=>handleClick(value)} key={index}>
                    <img 
                    //id={'img'+ value} 
                    src={'/image/Icons/'+ value +'.svg'}
                    alt={'This is '+ value +' option.'} 
                    />
                </button>
                )}     

                </div>
                <Add handleSubmit={handleSubmit}/>
        </div>
    );
}

export default InputSpend;
