import { Link } from "react-router-dom";
import Header from "../components/Header";
import Add from "../components/add";
import React from 'react';
import { useState } from "react";
import axios from "axios";

const incomeType = [
"salay",
"bonus",
"sideline",
"inves",
"allowance",
"stock",
"tips",
"othersIn"]

const InputIncome = () => {
    const [type, setType]= useState('');
    const sessionEmail = localStorage.getItem("email");

    const beforeStyle =  {
        width: '20%',

      };

    const selectStyle = {
        width: '20%',
        border: "2px solid rgb(204,123,105)",

    }

    const handleClick = (type) => {
        setType(type);
    }

    const handleSubmit = (note, date, amount) => {
        console.log("income", type, date, note, "$" + amount);

        axios
          .post("https://www.zoelindev.com/moneyCatBackend/income.php", {
            email: sessionEmail,
            type,
            date,
            note,
            amount,
          })
          .then((response) => {
            console.log(response);
          })
          .catch((error) => {
            console.log(error);
          });
      };


    return(
        <div className="inputIncomePage">
             <Header/>
            <div>
                <Link to="/input_spend" className="spend">Spend</Link>
                <Link to="/input_income" className="income" >Income</Link>
            </div>
            <div className="inputBox_grid">
                {incomeType.map((value, index) => (
                <button 
                style={type === value ? selectStyle : beforeStyle} 
                id={value} 
                onClick={()=>handleClick(value)} 
                key={index}>
                    <img 
                    //id={'img'+ value} 
                    src={'/image/Icons/'+ value +'.svg'}
                    alt={'This is '+ value +' option.'} 
                    />
                </button>
                ))}     
            </div>
            <Add handleSubmit={handleSubmit}/>
        </div>
    );
}

export default InputIncome;
