import { Link } from "react-router-dom";
import Header from "../components/Header";
import React from 'react';
import Open from '../components/Open';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faCalendarDays} from '@fortawesome/free-solid-svg-icons'
import { Doughnut } from "react-chartjs-2";
import { useState, useEffect } from "react";
import axios from "axios";


const ChartSpend = (props) => {
    const [data, setData] = useState({ labels: [], datasets: [] });
    const [isLoading, setIsLoading] = useState(true);

    const sessionEmail = localStorage.getItem("email");

    useEffect(() => {
      const fetchData = async () => {
        try {
          const userEmail = sessionEmail;
          const response = await axios.get(`https://www.zoelindev.com/moneyCatBackend/chartSpend.php?email=${userEmail}`);
          const spendData = response.data;

          const startDate = new Date();
          const billingStartDay = response.data.billing;

          startDate.setDate(billingStartDay);
          startDate.setHours(0, 0, 0, 0);

          const endDate = new Date(startDate);
          endDate.setMonth(endDate.getMonth() + 1);
          endDate.setDate(billingStartDay - 1);

          const filteredSpendData = spendData.filter((spend) => {
            const spendDate = new Date(spend.date);
            return spendDate >= startDate && spendDate <= endDate;
          });

          const totalSpendByType = filteredSpendData.reduce((acc, spend) => {
            acc[spend.type] = (acc[spend.type] || 0) + spend.cost;
            return acc;
          }, {});

          const labels = Object.keys(totalSpendByType);
          const datasets = [
            {
              data: Object.values(totalSpendByType),
              backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#7CBB00", "#E7E9ED", "#1F2D3D", "#F46B45", "#D73737", "#3B3F3E"],
              hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#7CBB00", "#E7E9ED", "#1F2D3D", "#F46B45", "#D73737", "#3B3F3E"],
            },
          ];

          setData({ labels, datasets });
          setIsLoading(false);
        } catch (error) {
          console.error(error);
        }
      };

      fetchData();
    }, []);

    if (isLoading) {
      return <p>Loading...</p>;
    }

    return (
        <div className="chartWrap">
            <Header/>
            <div className="round3"></div>
            <h2>Monthly</h2>

            <div className="op">
                <Link to="/chart_spend" className="chartSpendBtn">
                Spend / 
                </Link>
                <Link to="/chart_income" className="chartIncomeBtn">
                Income
                </Link>
            </div>

            <div className="top">
                <Doughnut data={data} className="chart"/>;
                <DateRangeForm email={sessionEmail} />
            </div>

            <div className="spendDetail">
                <Open title="Food">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Home">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Fun">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Travel">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Health">
                    <p>Hidden content for Food goes here</p>
                </Open>

                <Open title="Learn">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Shop">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Lend">
                    <p>Hidden content for Food goes here</p>
                </Open>
                <Open title="Others">
                    <p>Hidden content for Food goes here</p>
                </Open>
            </div>
        </div>
    );
}

export default ChartSpend;
