import { useState, useEffect } from "react";
import Header from "../components/Header";
import axios from 'axios';
//import { ReactSession } from 'react-client-session';
import { Link } from "react-router-dom";

const Overview = () => {
    const [totalCost, setTotalCost] = useState('');
    const [totalEarn, setTotalEarn] = useState('');
    const [balance, setBalance] = useState('');
    const [billingDate, setBillingDate] = useState(null);
    const [startDate, setStartDate] = useState();
    const [endDate, setEndDate] = useState();

    useEffect(() => {
        const fetchBillingDate = async () => {
          const sessionEmail = localStorage.getItem("email");
          console.log(sessionEmail);
          const response = await axios.post('https://www.zoelindev.com/moneyCatBackend/dateRangeForm.php', { email: sessionEmail });
          const data = response.data;
          setBillingDate(data.billing);
        };

        fetchBillingDate();
      }, []);

      const now = new Date();
      const monthStartDate = new Date(now.getFullYear(), now.getMonth()  - 1, billingDate);
      const monthEndDate = new Date(now.getFullYear(), now.getMonth() , billingDate - 1);
      const dbStartDate = monthStartDate.toISOString().slice(0, 10);
      const dbEndDate = monthEndDate.toISOString().slice(0, 10);

      setStartDate(dbStartDate);
      setEndDate(dbEndDate);
