import { useState, useEffect } from "react";
import Header from "../components/Header";
import axios from 'axios';
import { ReactSession } from 'react-client-session';

const Overview = () => {
  const [totalCost, setTotalCost] = useState('');
  const [totalEarn, setTotalEarn] = useState('');
  const [balance, setBalance] = useState('');

  useEffect(() => {
    async function fetchData() {
      try {
        // Store email in local storage
        localStorage.setItem("email", sessionEmail);

        // Retrieve email from local storage
        const sessionEmail = localStorage.get("email");
        console.log(sessionEmail);

        const response = await axios.post('https://www.zoelindev.com/moneyCatBackend/print.php', { email: sessionEmail });

        if (response.status === 200) {
          setTotalCost(response.data.totalCost);
          setTotalEarn(response.data.totalEarn);
          setBalance(response.data.totalEarn - response.data.totalCost);
        } else {
          console.log('Error: Status code ' + response.status);
        }
      } catch (error) {
        console.log('Error: ' + error.message);
      }
    }
    fetchData();
  }, []);

  return (
    <div className="overviewPage">
      <Header/>
      <img src="/image/sleeping.jpg" alt="A gril is sleeping with a fat cat" className="sleeping" width="1920" height="2559"/>
      <div className="flex_direction_grid">
        <p>Balance: {balance}</p>
        <div className="flex">
          <p>Spend: {totalCost}</p>
          <p>Income: {totalEarn}</p>
        </div>
      </div>
      <footer>
        <img src="/image/icons/tray.svg" alt="Icon for monthly chart" className="tray"/>
        <img src="/image/icons/overCat.svg" alt="Icon for overview page" className="overCat"/>
        <img src="/image/icons/calculater.svg" alt="Icon for bookkeeping calculater" className="calculater"/>
      </footer>
    </div>
  );
}

export default Overview;
