import { useState, useEffect } from "react";
import Header from "../components/Header";
import axios from 'axios';
//import { ReactSession } from 'react-client-session';
import { Link } from "react-router-dom";

const Overview = () => {
    const [totalCost, setTotalCost] = useState('');
    const [totalEarn, setTotalEarn] = useState('');
    const [balance, setBalance] = useState('');

  useEffect(() => {
    async function fetchData() {
      try {
        // Retrieve email from local storage
        const sessionEmail = localStorage.getItem("email");
        const sessionStartDate = localStorage.getItem("startDate");
        const sessionEndDate = localStorage.getItem("endDate");
        console.log(sessionEmail);


        useEffect(() => {
            const fetchBillingDate = async () => {
              const sessionEmail = localStorage.getItem("email");
              console.log(sessionEmail);
              const response = await axios.post('https://www.zoelindev.com/moneyCatBackend/dateRangeForm.php', { email: sessionEmail });
              const data = response.data;
              setBillingDate(data.billing);
            };

            fetchBillingDate();
          }, []);

        const now = new Date();
        const monthStartDate = new Date(now.getFullYear(), now.getMonth() + i - 1, billingDate);
        const monthEndDate = new Date(now.getFullYear(), now.getMonth() + i + 0, billingDate - 1);
        console.log(monthStartDate);

        const response = await axios.post('https://www.zoelindev.com/moneyCatBackend/print.php', {
            email: sessionEmail,
            startDate: sessionStartDate,
            endDate: sessionEndDate
        });

        if (response.status === 200) {
          setTotalCost(response.data.totalCost);
          setTotalEarn(response.data.totalEarn);
          setBalance(response.data.totalEarn - response.data.totalCost);
        } else {
          console.log('Error: Status code ' + response.status);
        }
      } catch (error) {
        console.log('Error: ' + error.message);
      }
    }
    fetchData();
  }, []);

  return (
    <div className="overviewPage">
      <Header/>
      <img src="/image/sleeping.jpg" alt="A gril is sleeping with a fat cat" className="sleeping" width="1920" height="2559"/>
      <div className="flex_direction_grid">
        <p>Balance: {balance}</p>
        <div className="flex">
          <p>Spend: {totalCost}</p>
          <p>Income: {totalEarn}</p>
        </div>
      </div>
      <footer>
        <Link to="/chart_spend">
        <img src="/image/icons/tray.svg" alt="Icon for monthly chart" className="tray"/></Link>

        <Link to="/overview">
        <img src="/image/icons/overCat.svg" alt="Icon for overview page" className="overCat"/></Link>

        <Link to="/input_spend">
        <img src="/image/icons/calculater.svg" alt="Icon for bookkeeping calculater" className="calculater"/></Link>
      </footer>
    </div>
  );
}

export default Overview;
