import { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faCalendarDays, faXmark} from '@fortawesome/free-solid-svg-icons'


//print optinon
const Option =(val)=>{
    return (<option value={val}>{val}</option>)
}
//add 0 before numbers before 10
const OptionZero =(val)=>{
    return (<option value={val}>0{val}</option>)
}
//select 01-31 roop
const select=[];
for (let i = 1; i <=31; i++){
    if(i<10){
        select.push(OptionZero(i));
    }else {
        select.push(Option(i));
    }
}

const Setting = () => {
    const [billingDate, setBillingDate]= useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log("submit works");
      }

    return(
        <div className="settingPage">

        <FontAwesomeIcon icon={faXmark} />
        <h1>Your Billing Cycle</h1>
        <p>Customized start date</p>

        <FontAwesomeIcon icon={faCalendarDays} />
        {/* <i class="fa-solid fa-calendar-days"></i> */}
        <form onSubmit={handleSubmit}>
            <label>Monthly Billing Date
            <select value={billingDate} onChange={(e)=> setBillingDate(e.target.value)}>
                {select}
            </select>
            </label>


            <input type="submit" value="Save"/>
        </form>

        <img src="/image/billingCat.png" alt="Get Money Cat" className="billingCat" width="2000" height="2000"/>
        </div>
    );
}

export default Setting;
