import React, { useState, useEffect } from 'react';
import axios from 'axios';

function List({ email, startDate, endDate, type }) {
  const [spendDetails, setSpendDetails] = useState([]);

  useEffect(() => {
    axios.post('https://www.zoelindev.com/moneyCatBackend/list.php', { email, startDate, endDate })
      .then((response) => {
        setSpendDetails(response.data[type] || []);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [email, startDate, endDate, type]);

  function formatDate(dateString) {
    const date = new Date(dateString);
    const month = new Intl.DateTimeFormat('en-US', { month: 'short' }).format(date);
    const day = date.getDate();
    return `${month} ${day}`;
  }

  return (
    <div>
      {spendDetails.length > 0 ? (
        <div>
          {spendDetails.map(({ date, note, cost }) => (
            <div key={`${date}-${note}-${cost}`}>
              {formatDate(date)} {note} {cost}
            </div>
          ))}
        </div>
      ) : (
        <p>No spend details found for {type}.</p>
      )}
    </div>
  );
}

export default List;
